  const keySubmit = (e) => {
    if (e.key === "Enter") {
      addset();
    }
  };


  ///Delete function
  const deleteItem = (id) => {
    const NewArray = items.filter((item) => item.id !== id);
    setItems(NewArray);
  };
  return (
    <div className="App">
      {/* Header */}
