import { useState } from 'react';
import './App.css';

function App() {
  const [newItem,setNewItem]=useState("ddd");
  const handleChange=(e)=>{
    const name=e.target.name;
    const value=e.target.value;
    setNewItem(values=>({...values,[name]:value}));

  }
  console.log(newItem);

  return (
    <div className="App">
{/* Header */}
      <h1>Todo List App</h1>
 {/* Input Field */}
      <input type="text" name='name' onChange={handleChange}
       placeholder="Add an item.." value={newItem ||""}>

      </input>
      <button>Add</button>
{/* Unordered List */}

<ul><li>Ali</li></ul>
    </div>
  );
}

export default App;
